import React, {useState } from 'react'
// import {Transition} from 'react-transition-group';
// import {CSSTransition} from 'react-transition-group';
// import {TransitionGroup} from 'react-transition-group';
import {Header} from './Header/Header'
import { Sidebar } from './Sidebar/Sidebar'
import {Footer} from './Footer/Footer'
import PropTypes from 'prop-types';
import { Infouser } from './InfoUser/Infouser';

import styles from './App.sass'


export const AppContext = React.createContext()

function App() {

  const [theme, setTheme] = useState(false)//состояние темы - режим темный/темный по умолчанию (theme=false)
  const  [menu, setMenu] = useState(false) // состояние меню - скрыто по умолчанию (menu=false)

  // отвечает за сменю надписи на button при смене темы
  const handleOnClickTheme = (event) => {

    if (event.target.textContent === "Light theme" || "Dark theme" )
      setTheme(!theme)

  }
  // показывает/закрывает панель меню
  const handleCallMenu = (event) => {

    setMenu(!menu)
  }

  let cssApp = theme ?
    "App App_Light":
    "App App_Dark"
  console.log(menu);

  return (

    <AppContext.Provider value={{handleOnClickTheme, theme, handleCallMenu, menu}}>
      <div className={cssApp}>

        <div  style={{width:"100%", height:"10vh"}}>
          <Header />
        </div>

        <div className={styles.App-menu}>
          <div style={{width:"300px", height:"79.5vh", padding:"0 0", flexWrap:"nowrap", position:"relative"}}>
            <Sidebar/>
          </div>

          <div style={{width:"100%", height:"90vh", position:"relative", top: "2em", textAlign:"center"}}>
            <Infouser/>
          </div>
        </div>


        <div style={{width:"100%", height: "10vh"}}>
          <Footer />
        </div>
      </div>
    </AppContext.Provider>

  );
}

export default App;

App.propsTypes = {
  handleOnClickTheme: PropTypes.func,
  handleCallMenu: PropTypes.func,

}
