import React from 'react';
import {useContext} from 'react';
import {List} from './List/List.jsx'
// import {Transition} from 'react-transition-group';
import {TransitionGroup} from 'react-transition-group';
// import '@animxyz/core';
// import { XyzTransition } from '@animxyz/react';


import { AppContext } from '../App';// контекст
import styles from './Sidebar.module.sass';


export function Sidebar() {


  const {theme} = useContext(AppContext)

  /*
   * theme = true светлый режим сайта/ false темный режим
   */
  let cssTheme = theme ?
  `${styles.Sidebar} ${styles.Sidebar_Light}`:
  `${styles.Sidebar} ${styles.Sidebar_Dark}`

  // console.log(cssTheme);

return (

  <TransitionGroup
  className={cssTheme}
  timeout={500}>

    <nav className={cssTheme}>
      <h5> Содержание: &nbsp;</h5>
      <List/>
      <code>
        Проект Resume создан c использованием технологии библиотеки React.
        <br />
        <a href="https://ru.reactjs.org"> На сайт React</a>
      </code>

    </nav>

  </TransitionGroup>


  )
}
