import PropTypes from 'prop-types';
import React, { useContext } from 'react';
import {useState} from 'react';
import '../information/information.json'
//import ReactDom from 'react-dom';

import { AppContext } from '../App';// контекст
import styles from './InfoAuthor.module.sass';


export function InfoAuthor() {

  const {theme} = useContext(AppContext)

  /*
   * theme = true светлый режим сайта/ false темный режим
   */
  let cssTheme = theme ?
  `${styles.infouser} ${styles.light}`:
  `${styles.infouser} ${styles.dark}`



  // const [state, setState] = useState();
  return (

    <div className={cssTheme}>
      <div id="screen" className={styles["informer-chart"]}>заставка</div>
      <div id="contact" className={styles["informer-chart"], styles["informer-chart-displayNone"]}>Мои контакты</div>
      <div id="author" className={styles["informer-chart"], styles["informer-chart-displayNone"]}>Обо мне</div>
      <div id="github" className={styles["informer-chart"], styles["informer-chart-displayNone"]}>Мой github</div>
      <div id="education" className={styles["informer-chart"], styles["informer-chart-displayNone"]}>Образование</div>
      <div id="skill" className={styles["informer-chart"], styles["informer-chart-displayNone"]}>Скилы</div>
    </div>

  )
}
