import PropTypes from 'prop-types';
import React, { useContext } from 'react';
// import {useState} from 'react';
// import info from '../information/information'

import { AppContext } from '../App';// контекст
import styles from './InfoAuthor.module.sass';


export function InfoAuthor() {

  const {theme, infoAutor} = useContext(AppContext)

  /*
   * theme = true светлый режим сайта/ false темный режим
   */
  let cssTheme = theme ?
  `${styles.infoAuthor} ${styles.light}`:
  `${styles.infoAuthor} ${styles.dark}`

  const arrayDiv =
  {
    contact : (<div id="contact" className={styles["infoAuthor-chart"], styles["infoAuthor-chart-displayNone"]}>Мои контакты</div>),

    autor : (<div id="author" className={styles["infoAuthor-chart"], styles["infoAuthor-chart-displayNone"]}>Обо мне</div>),

    github : (<div id="github" className={styles["infoAuthor-chart"], styles["infoAuthor-chart-displayNone"]}>Мой github</div>),

    education : (<div id="education" className={styles["infoAuthor-chart"], styles["infoAuthor-chart-displayNone"]}>Образование</div>),

    skill : (<div id="skill" className={styles["infoAuthor-chart"], styles["infoAuthor-chart-displayNone"]}>Скилы</div>)
  }
  console.log(arrayDiv);

  return (

    <div className={cssTheme}>
      {arrayDiv.screen}

      {/* <div id="screen" className={styles["infoAuthor-chart"]}>заставка</div>*/}
    </div>

  )
}
