import PropTypes from 'prop-types';
import React, { useContext} from 'react';
import screen from './istockphoto-1173458627-612x612.jpg';
import phone from './phone.svg'
// import {useState} from 'react';
// import info from '../information/information'

import { AppContext } from '../App';// контекст
import styles from './InfoAuthor.module.sass';


export function InfoAuthor() {

  const {theme, infoAutor} = useContext(AppContext)

  /*
   * theme = true светлый режим сайта/ false темный режим
   */
  let cssTheme = theme ?
  `${styles.infoAuthor} ${styles.light}`:
  `${styles.infoAuthor} ${styles.dark}`

  const arrayDiv =
  {
    screen : (<div id="screen"
             className={`${styles["infoAuthor-chart"]} ${styles.screen}`}
             style={{backgroundImage: `url(${screen})`, color:"red"}}>
             </div>),

    contact : (<div id="contact"
              className={styles["infoAuthor-chart"]}>
                <ol className={`${cssTheme} ${styles["infoAuthor-chart-ol"]}`}>
                  <li>
                    <img src={phone} alt="logo phone"/>
                    <h3>телефон 8(995)313-78-15</h3>
                  </li>
                  <li>
                    <a className={cssTheme} href="https://t.me/Goodweb3">Телеграм</a>
                  </li>
                  <li>
                    <a className={cssTheme} href="https://twitter.com/SKYNET64927015">Twitter</a>
                  </li>
                  <li>email esaul-00@mail.ru</li>

                </ol>
              </div>),

    author : (<div id="author" className={styles["infoAuthor-chart"]}>Обо мне</div>),

    github : (<div id="github" className={styles["infoAuthor-chart"]}>Мой github</div>),

    education : (<div id="education" className={styles["infoAuthor-chart"]}>Образование</div>),

    skill : (<div id="skill" className={styles["infoAuthor-chart"]}>Скилы</div>)
  }

  console.log(arrayDiv.contact);

  return (

    <div className={cssTheme}>
      {arrayDiv[infoAutor]}
    </div>

  )
}
