import React from 'react';
import {useContext} from 'react';
import '@animxyz/core'
import { XyzTransition } from '@animxyz/react'

import { AppContext } from '../App';
import styles from './Sidebar.module.sass';


export function Sidebar() {


  const {theme, numberTask} = useContext(AppContext)

  /*
   * theme = true светлый режим сайта/ false темный режим
   */
  let cssTheme = theme ?
  styles.Sidebar + ' ' + styles.Sidebar_Light:
  styles.Sidebar + ' ' + styles.Sidebar_Dark

  return (
    <XyzTransition appear xyz="fade down flip-up left perspective-5">
      <nav className={cssTheme}>
        <h5>
          Количество задач в списке: &nbsp;
          <span>{numberTask}</span>
        </h5>
        <code>
          Проект создан в учебных целях, для понимания технологии React.
          <br />
          <a href="https://ru.reactjs.org"> На сайт React</a>
          </code>
      </nav>
    </XyzTransition>

  )
}
